export class R2Storage {
  constructor(private bucket: R2Bucket) {}

  async upload(key: string, data: ArrayBuffer | string, contentType?: string): Promise<R2Object> {
    return this.bucket.put(key, data, {
      httpMetadata: { contentType: contentType ?? 'application/octet-stream' }
    });
  }

  async download(key: string): Promise<R2ObjectBody | null> {
    return this.bucket.get(key);
  }

  async getSignedUrl(key: string, expiresInSeconds = 3600): Promise<string> {
    // R2 presigned URLs require a custom implementation
    // For now, return a path that goes through the worker
    return `/api/files/${key}?expires=${Date.now() + expiresInSeconds * 1000}`;
  }

  async delete(key: string): Promise<void> {
    await this.bucket.delete(key);
  }

  async list(prefix?: string): Promise<R2Objects> {
    return this.bucket.list({ prefix });
  }
}
```

