export async function backupD1ToR2(
  db: D1Database,
  backups: R2Bucket,
  dbName: string
): Promise<string> {
  const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
  const key = `${dbName}/${timestamp}.sql`;

  // Export via D1 (requires special handling)
  // For now, store a snapshot marker
  const marker = JSON.stringify({
    database: dbName,
    timestamp,
    note: 'D1 Time Travel available for 30 days'
  });

  await backups.put(key, marker);
  return key;
}
