```typescript
export class TenantState extends DurableObject {
  private requestCounts = new Map<string, { count: number; windowStart: number }>();

  async checkRateLimit(key: string, limit: number, windowMs = 60000): Promise<{ allowed: boolean; remaining: number }> {
    const now = Date.now();
    const state = this.requestCounts.get(key) ?? { count: 0, windowStart: now };

    if (now - state.windowStart > windowMs) {
      state.count = 0;
      state.windowStart = now;
    }

    state.count++;
    this.requestCounts.set(key, state);

    return {
      allowed: state.count <= limit,
      remaining: Math.max(0, limit - state.count),
    };
  }

  async getUsage(): Promise<{ requests: number; lastActivity: number }> {
    let total = 0;
    let lastActivity = 0;
    for (const [, state] of this.requestCounts) {
      total += state.count;
      lastActivity = Math.max(lastActivity, state.windowStart);
    }
    return { requests: total, lastActivity };
  }
}
```

