export interface Env {}

interface TraceEvent {
  scriptName: string;
  outcome: string;
  eventTimestamp: number;
  logs: Array<{ message: string[]; level: string; timestamp: number }>;
  exceptions: Array<{ name: string; message: string }>;
}

export default {
  async tail(events: TraceEvent[], env: Env): Promise<void> {
    for (const event of events) {
      // Structure logs for analysis
      const trace = {
        worker: event.scriptName,
        outcome: event.outcome,
        timestamp: new Date(event.eventTimestamp).toISOString(),
        logs: event.logs.map(l => ({ level: l.level, message: l.message.join(' ') })),
        errors: event.exceptions.map(e => ({ name: e.name, message: e.message })),
      };

      // For now, log to console (can be extended to Logpush)
      console.log(JSON.stringify(trace));
    }
  }
};
```

