```typescript
import { WorkflowEntrypoint, WorkflowEvent, WorkflowStep } from 'cloudflare:workers';

interface WorkflowParams {
  workflowId: string;
  tenantId: string;
  config: Record<string, unknown>;
}

export class WorkflowExecutor extends WorkflowEntrypoint<Env, WorkflowParams> {
  async run(event: WorkflowEvent<WorkflowParams>, step: WorkflowStep) {
    const { workflowId, tenantId, config } = event.payload;

    // Step 1: Validate and prepare
    const validated = await step.do('validate', async () => {
      // Validate config, check permissions
      return { valid: true, normalizedConfig: config };
    });

    // Step 2: Execute main logic (automatically retried on failure)
    const result = await step.do('execute', async () => {
      // Main workflow execution
      return { success: true, data: {} };
    });

    // Step 3: Can sleep for extended periods
    if (config.delayMinutes) {
      await step.sleep('delay', `${config.delayMinutes} minutes`);
    }

    // Step 4: Finalize
    await step.do('finalize', async () => {
      // Record completion, send notifications
    });

    return result;
  }
}

export default {
  async fetch(request: Request, env: Env) {
    // Trigger workflow via HTTP
    const { workflowId, tenantId, config } = await request.json();
    const instance = await env.WORKFLOW.create({ params: { workflowId, tenantId, config } });
    return Response.json({ instanceId: instance.id });
  }
};
```

